mapper.select:
SELECT

FROM blog_article

	AND del_flag != '1'
	
		AND remarks LIKE concat(concat('%',#{remarks}),'%')
	
	
		AND tags = #{tags}
	
	
		AND author = #{author}
	
	
		AND allow_comment = #{allowComment}
	
	
		AND is_carousel = #{isCarousel}
	

ORDER BY sort,updateDate desc

        select count(0) from blog_article
        
		AND del_flag != '1'
			
				AND remarks LIKE concat(concat('%',#{remarks}),'%')
			
			
				AND tags = #{tags}
			
			
				AND author = #{author}
			
			
				AND allow_comment = #{allowComment}
			
			
				AND is_carousel = #{isCarousel}
